echo "contents_hash,dirname,basename,inode,device,permissions,user_owner,group_owner,last_accessed,last_modified,last_changed,inode_birth,user_flags,links_to_file,size" > filewalk; sudo find / -type f -exec  sh -c 'A=$(shasum "$0" | cut -d" "  -f1-2 | tr -d " ") ; DIR="$(dirname "$0")/"; BASE=$(basename "$0"); B=$(stat -f  "%i,%d,%p,%Su,%Sg,%a,%m,%c,%B,%f,%l,%z" "$0"); echo $A,$DIR,$BASE,$B >> filewalk ; ' {} \;
